import { useState } from 'react';

function Square({ value, winningSquare, onSquareClick }) {
  const className = winningSquare ? "winning-square" : "square"
  return <button className={className} onClick={onSquareClick}>{value}</button>;
}

function Board({ xIsNext, squares, onPlay }) {
  function handleClick(i) {
    if (calculateWinner(squares) || squares[i][0]) {
      return;
    }
    const nextSquares = squares.slice();
    if (xIsNext) {
      nextSquares[i] = ["X", false];
    } else {
      nextSquares[i] = ["O", false];
    }
    onPlay(nextSquares);
  }

  console.log(squares);

  const winningSquares = calculateWinner(squares)
  let status;
  if (winningSquares) {
    const winner = squares[winningSquares[0]][0];
    status = "Winner: " + winner;
    winningSquares.forEach((index) => squares[index][1] = true);
  } else if (
    (() => {
      let full = true;
      for (let i=0; i<squares.length; i++) {
        if (squares[i][0] === null) {
          full = false;
          break;
        }
      }
      return full;
    })()
  ) {
    status = "It's a draw!";
  } else {
    status = "Next player: " + (xIsNext ? "X" : "O");
  }

  console.log(squares);

  return (
    <>
      <div className="status">{status}</div>

      <div className="board-row">
        {() => {
          for (i=0; i<9; i++) {
            if (i % 3 === 0) {
              <></>
            }
            return <Square value={squares[i]} winningSquare={squares[i][1]} onSquareClick={() => handleClick(i)} />
          }
        }}
      </div>

      <div className="board-row">
        <Square value={squares[0]} winningSquare={squares[0][1]} onSquareClick={() => handleClick(0)} />
        <Square value={squares[1]} winningSquare={squares[1][1]} onSquareClick={() => handleClick(1)} />
        <Square value={squares[2]} winningSquare={squares[2][1]} onSquareClick={() => handleClick(2)} />
      </div>
      <div className="board-row">
        <Square value={squares[3]} winningSquare={squares[3][1]} onSquareClick={() => handleClick(3)} />
        <Square value={squares[4]} winningSquare={squares[4][1]} onSquareClick={() => handleClick(4)} />
        <Square value={squares[5]} winningSquare={squares[5][1]} onSquareClick={() => handleClick(5)} />
      </div>
      <div className="board-row">
        <Square value={squares[6]} winningSquare={squares[6][1]} onSquareClick={() => handleClick(6)} />
        <Square value={squares[7]} winningSquare={squares[7][1]} onSquareClick={() => handleClick(7)} />
        <Square value={squares[8]} winningSquare={squares[8][1]} onSquareClick={() => handleClick(8)} />
      </div>
    </>
  );
}

export default function Game() {
  const [history, setHistory] = useState([Array(9).fill([null, false])]);
  const [currentMove, setCurrentMove] = useState(0);
  const currentSquares = history[currentMove];
  const xIsNext = currentMove % 2 === 0;

  function handlePlay(nextSquares) {
    // Stringifiy nextSquares to create a deep copy and prevent same object reference throughout entire history
    const nextHistory = [...history.slice(0, currentMove + 1), JSON.parse(JSON.stringify(nextSquares))];
    setHistory(nextHistory);
    setCurrentMove(nextHistory.length - 1);
  }

  function jumpTo(nextMove) {
    setCurrentMove(nextMove);
  }

  const moves = history.map((squares, move) => {
    let description;
    if (move > 0) {
      description = 'Go to #' + move;
    } else {
      description = 'Go to game start';
    }
    return (
      <li key={move}>
        <button onClick={() => jumpTo(move)}>{description}</button>
      </li>
    );
  });

  return (
    <div className="game">
      <div className="gameboard">
        <Board xIsNext={xIsNext} squares={currentSquares} onPlay={handlePlay} />
      </div>
      <div className="game-info">
        <ol>{moves}</ol>
      </div>
    </div>
  );
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i=0; i<lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a][0] && squares[a][0] === squares[b][0] && squares[a][0] === squares[c][0]) {
      return [a, b, c];
    }
  }
  return null;
}
